<script>import { spring } from 'svelte/motion';
import { format as formatUtil } from '../utils/format';
export let value;
export let format = undefined;
export let options = undefined;
export let disabled = false;
const springValue = spring(value, options);
$: $springValue = value ?? 0;
$: displayValue = disabled || value == null ? value : $springValue;
</script>

<slot value={displayValue}>
  {#if format}
    {formatUtil(displayValue, format)}
  {:else}
    {displayValue}
  {/if}
</slot>
